const {Header, Content, Footer} = Layout;

const DemoBox = props => <p style={{height: props.value, backgroundColor: "rgb(255,0,0)"}}>{props.children}</p>;

const Dessert = props => {
    const {data} = props

    return (
        <>
            {data.map((dessert) => (
                <>
                    <Divider orientation="left"><Typography.Title level={3}>{dessert.name}</Typography.Title></Divider>
                    <Col span={6}>

                        {isiOS()
                            ?
                            <a href={dessert.ar}>
                                <Image
                                    width={"100%"}
                                    src={dessert.image}
                                    placeholder={"data:image/png;base64,"}
                                    preview={false}
                                    alt={dessert.name}
                                />
                            </a>
                            :
                            <Image
                                width={"100%"}
                                src={dessert.image}
                                placeholder={"data:image/png;base64,"}
                                preview={false}
                                alt={dessert.name}
                            />
                        }

                    </Col>
                    <Col span={14}>
                        <Row justify="space-around" align="top">
                            <Col xs={{span: 10}} span={18}>
                                {dessert.ingredients.map((ingredient) => <Typography
                                    className={"ingredients_menu"}>{ingredient.toLowerCase()}</Typography>)}
                            </Col>
                            <Col span={4}>
                                <Typography className={"menu_price"}>{dessert.price}</Typography>
                            </Col>
                        </Row>
                    </Col>

                </>
            ))}


        </>
    )

}

