export const CurrentMenuPage = (props) => {

    const antIcon = <LoadingOutlined spin style={{fontSize: "50vw"}}/>;

    const dispatch = useDispatch()

    const currentMenu = useSelector(currentMenuSelector)
    const errors = useSelector(currentMenuErrorsSelector)

    useEffect(() => {
        dispatch(initCurrentMenu())


        return () => {
        }

    }, [dispatch])

    if (errors.length) return (


        <div style={{
            width: "100vw",
            height: "100vh",
            verticalAlign: "middle",
            display: "table-cell",
            textAlign: "center"
        }}>

            <List
                style={{display: "inline-grid"}}
                dataSource={errors}
                renderItem={error => (
                    <List.Item>
                        <Typography.Title type="danger" strong={true}>{error}</Typography.Title>
                    </List.Item>
                )}
            />
        </div>
    )


    if (currentMenu) return (

        <>
            <Divider orientation="center"><Typography.Title>Desserts</Typography.Title></Divider>
            <Row justify="space-around" align="top">
                <Col span={6}>
                </Col>
                <Col span={14}>
                    <Row justify="space-around" align="top">
                        <Col xs={{span: 10}} span={18}>
                            <Divider orientation="center">
                                <Typography.Title level={4} className={"inner_title"}>Ingredients</Typography.Title>
                            </Divider>
                        </Col>
                        <Col span={4}>
                            <Divider orientation="center">
                                <Typography.Title level={4} className={"inner_title"}>Price</Typography.Title>
                            </Divider>
                        </Col>
                    </Row>
                </Col>
                <Dessert data={currentMenu.desserts}/>
            </Row>
            <Divider orientation="center"><Typography.Title>Hot drinks</Typography.Title></Divider>
            <Row justify="space-around" align="middle">
                <Col span={10}>
                    <Divider orientation="center">
                        <Typography.Title level={4} className={"inner_title"}>Volume</Typography.Title>
                    </Divider>
                </Col>
                <Col span={10}>
                    <Divider orientation="center">
                        <Typography.Title level={4} className={"inner_title"}>Price</Typography.Title>
                    </Divider>
                </Col>
                <HotDrink data={currentMenu.hotDrinks}/>
            </Row>
            <Divider orientation="center"><Typography.Title>Alcohol</Typography.Title></Divider>
            <Row justify="space-around" align="middle">
                <Col span={10}>
                    <Divider orientation="center">
                        <Typography.Title level={4} className={"inner_title"}>Volume</Typography.Title>
                    </Divider>
                </Col>
                <Col span={10}>
                    <Divider orientation="center">
                        <Typography.Title level={4} className={"inner_title"}>Price</Typography.Title>
                    </Divider>
                </Col>
                <Alcohol data={currentMenu.alcohol}/>
            </Row>
        </>
    )

    return (
        <div style={{position: "fixed", top: "25%", left: "25%"}}>
            <Spin indicator={antIcon}/>
        </div>
    )
}